import React, { useState, useEffect, useRef } from "react";
import "./TaskBoard.css";
import Card from "./Card";
import { useNavigate } from "react-router-dom";

function TaskBoard(props) {
  const [count, setCount] = useState(0);
  const [list, setList] = useState(props.value);
  const [dragging, setDragging] = useState(false);

  useEffect(() => {
    setList(props.value);
  }, [setList, props.value]);

  const dragItem = useRef();
  const dragItemNode = useRef();

  const handletDragStart = (e, item) => {
    console.log("Starting to drag", item);

    dragItemNode.current = e.target;
    dragItemNode.current.addEventListener("dragend", handleDragEnd);
    dragItem.current = item;

    setTimeout(() => {
      setDragging(true);
    }, 0);
  };
  const handleDragEnter = (e, targetItem) => {
    console.log("Entering a drag target", targetItem);
    if (dragItemNode.current !== e.target) {
      console.log("Target is NOT the same as dragged item");
      setList((oldList) => {
        let newList = JSON.parse(JSON.stringify(oldList));
        newList[targetItem.grpI].items.splice(
          targetItem.itemI,
          0,
          newList[dragItem.current.grpI].items.splice(
            dragItem.current.itemI,
            1
          )[0]
        );
        dragItem.current = targetItem;
        localStorage.setItem("List", JSON.stringify(newList));
        return newList;
      });
    }
  };
  const handleDragEnd = (e) => {
    setDragging(false);
    dragItem.current = null;
    dragItemNode.current.removeEventListener("dragend", handleDragEnd);
    dragItemNode.current = null;
  };
  const getStyles = (item) => {
    if (
      dragItem.current.grpI === item.grpI &&
      dragItem.current.itemI === item.itemI
    ) {
      return "dnd-item current";
    }
    return "dnd-item";
  };

  const Navigate = useNavigate();
  const item = props.data;
  const checkbox = (d, id) => {
    console.log(d.target.checked);
    if (d.target.checked) {
      setCount(count + 1);
      const dataM = item.filter((data) => data.id !== id);
      props.setData(dataM);
    }
  };

  return (
    <div>
      <div className="taskNav">
        <img src="taskLogo.png" alt="task-logo" />
        <h1>Task Board </h1>
        <input
          type="button"
          value="Add Task"
          onClick={() => Navigate("/add/id")}
        />
      </div>

      {
        list ? (
          <div className="drag-n-drop">
            {list.map((grp, grpI) => (
              <div
                key={grp.title}
                onDragEnter={
                  dragging && !grp.items.length
                    ? (e) => handleDragEnter(e, { grpI, itemI: 0 })
                    : null
                }
                className="dnd-group"
              >
                {grp.items.map((item, itemI) => (
                  <div
                    draggable
                    key={item}
                    onDragStart={(e) => handletDragStart(e, { grpI, itemI })}
                    onDragEnter={
                      dragging
                        ? (e) => {
                            handleDragEnter(e, { grpI, itemI });
                          }
                        : null
                    }
                    className={
                      dragging ? getStyles({ grpI, itemI }) : "dnd-item"
                    }
                  >
                    <Card
                      key={index.id}
                      value={index}
                      setData={props.setData}
                      {...props}
                      fun={checkbox}
                      setTogal={props.setTogal}
                    />
                  </div>
                ))}
              </div>
            ))}
          </div>
        ) : null

        // item.map((index) => {
        //   return (
        //     <Card
        //       key={index.id}
        //       value={index}
        //       setData={props.setData}
        //       {...props}
        //       fun={checkbox}
        //       setTogal={props.setTogal}
        //     />
        //   );
        // })
      }

      <div className="bottom">
        <div className="dropup">
          <button className="dropbtn count-btn">Completed({count})â–²</button>
          <div className="dropup-content">
            <div
              className="count"
              style={{ display: "flex", padding: "20px" }}
              onClick={() => setCount(0)}
            >
              <p>Clear All</p>
              <img src="delete.png" alt="delet" className="count-del" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TaskBoard;
