import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Add.css";
function Add(props) {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [note, setNote] = useState("");

  const navigate = useNavigate();
  const values = () => {
    const data = {
      id: Math.random().toString(),
      title: title,
      date: new Date(date),
      note: note,
    };
    props.setData((prevdata) => [...prevdata, data]);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-25">
          <label htmlFor="Title">Title</label>
        </div>
        <div className="col-75">
          <input
            type="text"
            className="Title"
            name="Title"
            placeholder="Your Title.."
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="date">Date</label>
        </div>
        <div className="col-75">
          <input
            type="date"
            className="date"
            name="date"
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="day">Day</label>
        </div>
        <div className="col-75">
          <select className="date-range form-control" name="date">
            <option value="yest">{yest} (Yesterday) </option>
            <option selected="selected" value="dateh">
              {dateh} (Today)
            </option>
            <option value="tomorrow">{tom} (Tomorrow) </option>
            <option value="week">{week} (week) </option>
            <option value="nextmonth">{month} (month) </option>
          </select>
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="Note">Note</label>
        </div>
        <div className="col-75">
          <textarea
            className="note"
            name="subject"
            placeholder="Write something.."
            style={{ height: "200px" }}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <input
          type="button"
          value="Save"
          className="btn1"
          onClick={() => {
            navigate("/");
            values();
          }}
        />
        <input
          type="button"
          value="Cancel"
          className="btn2"
          onClick={() => navigate("/")}
        />
      </div>
    </div>
  );
}

export default Add;
