import React, { useState } from "react";
import "./TaskBoard.css";
import Card from "./Card";
import { useNavigate } from "react-router-dom";
import { HTML5Backend } from 'react-dnd-html5-backend'
import { DndProvider } from 'react-dnd'
function TaskBoard(props) {
  const [count, setCount] = useState(0);
  const Navigate = useNavigate();
  const item = props.data;
  const checkbox = (d, id) => {
    console.log(d.target.checked);
    if (d.target.checked) {
      setCount(count + 1);
      const dataM = item.filter((data) => data.id !== id);
      props.setData(dataM);
    }
  };
  return (
    <div>
      <div className="taskNav">
        <img src="taskLogo.png" alt="task-logo" />
        <h1>Task Board </h1>
        <input
          type="button"
          value="Add Task"
          onClick={() => Navigate("/add/id")}
        />
      </div>
      <DndProvider backend={HTML5Backend}>
      <div className="Task-container">
        {item.map((index) => {
          return (
            <Card
              key={index.id}
              value={index}
              setData={props.setData}
              {...props}
              fun={checkbox}
              setTogal={props.setTogal}
            />
          );
        })}
      </div>
      </DndProvider>
      <div className="bottom">
        <div className="dropup">
          <button className="dropbtn count-btn">Completed({count})â–²</button>
          <div className="dropup-content">
            <div
              className="count"
              style={{ display: "flex", padding: "20px" }}
              onClick={() => setCount(0)}
            >
              <p>Clear All</p>
              <img src="delete.png" alt="delet" className="count-del" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TaskBoard;
