import React, { useState } from "react";
import "./TaskBoard.css";
import CompleteCount from "./CompleteCount";
import Card from "./Card";
import { useNavigate } from "react-router-dom";
import Tippy from "@tippyjs/react";
import "tippy.js/dist/tippy.css";

function TaskBoard(props) {
  const [count, setCount] = useState(0);
  const [visible, setVisible] = useState(false);
  const show = () => setVisible(true);
  const hide = () => setVisible(false);

  const Navigate = useNavigate();
  const item = props.data;
  return (
    <div>
      <div className="taskNav">
        <img src="taskLogo.png" alt="task-logo" />
        <h1>Task Board </h1>
        <input
          type="button"
          value="Add Task"
          onClick={() => Navigate("/add")}
        />
      </div>

      <div className="Task-container">
        {item.map((index) => {
          return (
            <Card
              key={index.id}
              value={index}
              setData={props.setData}
              {...props}
            />
          );
        })}
      </div>

      <div className="bottom">
        {/* <Tippy
          render={(attrs) => (
            <CompleteCount className="box" tabIndex="-1" {...attrs} />
          )}
        >
          <div onClick={visible ? hide : show}>Completed(0)▲</div>
        </Tippy> */}
        <Tippy
          content={<CompleteCount setCount={setCount} />}
          visible={visible}
          onClickOutside={hide}
        >
          <button className="count-btn">
            Completed({count})▲
          </button>
        </Tippy>
      </div>
    </div>
  );
}

export default TaskBoard;
