import React, { useState } from "react";
import "./TaskBoard.css";
import Card from "./Card";
import { useNavigate } from "react-router-dom";
import { useDrop } from "react-dnd";
function TaskBoard(props) {
  const [count, setCount] = useState(0);
  const [board, setBoard] = useState([]);
  const Navigate = useNavigate();
  const item = props.data;
  const checkbox = (d, id) => {
    console.log(d.target.checked);
    if (d.target.checked) {
      setCount(count + 1);
      const dataM = item.filter((data) => data.id !== id);
      props.setData(dataM);
    }
  };

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "card",
    drop: (item) => addCardToBoard(item.id),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));
  console.log(isOver);
  const addCardToBoard = (id) => {
    const cardList = item.filter((item) => id === item.id);
    props.setBoard((board) => [...board, cardList[0]]);
  };

  return (
    <div>
      <div className="taskNav">
        <img src="taskLogo.png" alt="task-logo" />
        <h1>Task Board </h1>
        <input
          type="button"
          value="Add Task"
          onClick={() => Navigate("/add/id")}
        />
      </div>

      <div className="Task-container" ref={drop}>
        {item.map((index) => {
          return (
            <Card
              key={index.id}
              value={index}
              setData={props.setData}
              {...props}
              fun={checkbox}
              setTogal={props.setTogal}
            />
          );
        })
        }
      </div>
      <div className="bottom">
        <div className="dropup">
          <button className="dropbtn count-btn">Completed({count})â–²</button>
          <div className="dropup-content">
            <div
              className="count"
              style={{ display: "flex", padding: "20px" }}
              onClick={() => setCount(0)}
            >
              <p>Clear All</p>
              <img src="delete.png" alt="delet" className="count-del" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TaskBoard;
