import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./Add.css";
function Update(props) {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [note, setNote] = useState("");
  const { id } = useParams();
  console.log(id);
  const current = new Date();
  const dateh = `${current.getFullYear()}-${
    current.getMonth() + 1 < 10
      ? "0" + (current.getMonth() + 1)
      : current.getMonth() + 1
  }-${current.getDate() < 10 ? "0" + current.getDate() : current.getDate()}`;

  const yesterday = new Date(current);
  yesterday.setDate(yesterday.getDate() - 1);
  const yest = `${yesterday.getFullYear()}-${
    yesterday.getMonth() + 1 < 10
      ? "0" + (yesterday.getMonth() + 1)
      : yesterday.getMonth() + 1
  }-${
    yesterday.getDate() < 10 ? "0" + yesterday.getDate() : yesterday.getDate()
  }`;

  const tomorrow = new Date(current);
  tomorrow.setDate(tomorrow.getDate() + 1);
  const tom = `${tomorrow.getFullYear()}-${
    tomorrow.getMonth() + 1 < 10
      ? "0" + (tomorrow.getMonth() + 1)
      : tomorrow.getMonth() + 1
  }-${tomorrow.getDate() < 10 ? "0" + tomorrow.getDate() : tomorrow.getDate()}`;

  const nextWeek = new Date(current);
  nextWeek.setDate(nextWeek.getDate() + 7);
  const week = `${nextWeek.getFullYear()}-${
    nextWeek.getMonth() + 1 < 10
      ? "0" + (nextWeek.getMonth() + 1)
      : nextWeek.getMonth() + 1
  }-${nextWeek.getDate() < 10 ? "0" + nextWeek.getDate() : nextWeek.getDate()}`;

  const nextMonth = new Date(current);
  nextMonth.setMonth(nextMonth.getMonth() + 1);
  const month = `${nextMonth.getFullYear()}-${
    nextMonth.getMonth() + 1 < 10
      ? "0" + (nextMonth.getMonth() + 1)
      : nextMonth.getMonth() + 1
  }-${
    nextMonth.getDate() < 10 ? "0" + nextMonth.getDate() : nextMonth.getDate()
  }`;

  const dateChange = (day) => {
    if (day === "today") {
      setDate(dateh);
    } else if (day === "yesterday") {
      setDate(yest);
    } else if (day === "tomorrow") {
      setDate(tom);
    } else if (day === "nextWeek") {
      setDate(week);
    } else if (day === "nextmonth") {
      setDate(month);
    }
  };
  let fdata = "";
  const navigate = useNavigate();

  const values = () => {
    let data = "";
    if (title && date && note) {
      data = {
        id: Math.random().toString(),
        title: title,
        date: new Date(date),
        note: note,
      };
      props.setData((prevdata) => [...prevdata, data]);
      navigate("/");
    } else {
      alert("All fields are mandetory,please enter data");
    }
  };
  fdata = props.data.filter((index) => index.id === id);
  console.log(fdata[0].title);
  // setDate(fdata[0].date)
  return (
    <div className="container">
      <div className="row">
        <div className="col-25">
          <label htmlFor="Title">Title</label>
        </div>
        <div className="col-75">
          <input
            type="text"
            className="Title"
            name="Title"
            placeholder={fdata[0].title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="date">Date</label>
        </div>
        <div className="col-75">
          <input
            type="date"
            className="date"
            name="date"
            placeholder={fdata[0].d}
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="day">Day</label>
        </div>
        <div className="col-75">
          <select
            className="date-range form-control"
            name="date"
            onChange={(e) => dateChange(e.target.value)}
          >
            <option value="none" checked disabled hidden>
              Select Date
            </option>
            <option value="yesterday">Yesterday</option>
            <option select="true" value="today">
              Today
            </option>
            <option value="tomorrow">Tomorrow</option>
            <option value="nextWeek">Next Week</option>
            <option value="nextmonth">Next Month</option>
          </select>
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="Note">Note</label>
        </div>
        <div className="col-75">
          <textarea
            className="note"
            name="subject"
            placeholder={fdata[0].note}
            style={{ height: "200px" }}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <input
          type="button"
          value="Update"
          className="btn1"
          onClick={() => {
            values();
          }}
        />
        <input
          type="button"
          value="Cancel"
          className="btn2"
          onClick={() => navigate("/")}
        />
      </div>
    </div>
  );
}

export default Update;
