import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Add.css";
function Add(props) {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState();
  const [note, setNote] = useState("");
let test=date
console.log(test);
  const current = new Date();

  const dateh = `-0${
    current.getMonth() + 1
  }--${current.getDate()}`;

  const yesterday = new Date(current);
  yesterday.setDate(yesterday.getDate() - 1);
  const yest = `${yesterday.getDate()}-${
    yesterday.getMonth() + 1
  }-${yesterday.getFullYear()}`;

  const tomorrow = new Date(current);
  tomorrow.setDate(tomorrow.getDate() + 1);
  const tom = `${tomorrow.getDate()}-${
    tomorrow.getMonth() + 1
  }-${tomorrow.getFullYear()}`;

  const nextWeek = new Date(current);
  nextWeek.setDate(nextWeek.getDate() + 7);
  const week = `${nextWeek.getDate()}-${
    nextWeek.getMonth() + 1
  }-${nextWeek.getFullYear()}`;

  const nextMonth = new Date(current);
  nextMonth.setDate(nextMonth.getDate() + 30);
  const month = `${nextMonth.getDate()}-${
    nextMonth.getMonth() + 1
  }-${nextMonth.getFullYear()}`;

  const dateChange = (day) => {
    if (day === "today") {
      setDate(dateh);
      alert(date)
    } else if (day === "yesterday") {
      setDate(yest);
    } else if (day === "tomorrow") {
      setDate(tom);
    } else if (day === "nextWeek") {
      setDate(nextWeek);
    } else if (day === "nextMonth") {
      setDate(nextMonth);
    }

  };

  const navigate = useNavigate();

  const values = () => {
    const data = {
      id: Math.random().toString(),
      title: title,
      date: new Date(date),
      note: note,
    };
    props.setData((prevdata) => [...prevdata, data]);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-25">
          <label htmlFor="Title">Title</label>
        </div>
        <div className="col-75">
          <input
            type="text"
            className="Title"
            name="Title"
            placeholder="Your Title.."
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="date">Date</label>
        </div>
        <div className="col-75">
          <input
            type="date"
            className="date"
            name="date"
            value={test}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="day">Day</label>
        </div>
        <div className="col-75">
          <select
            className="date-range form-control"
            name="date"
            onChange={(e) => dateChange(e.target.value)}
          >
            <option value="Yesterday">{yest} (Yesterday) </option>
            <option selected="selected" value="today">
              {dateh} (Today)
            </option>
            <option value="tomorrow">{tom} (Tomorrow) </option>
            <option value="week">{week} (week) </option>
            <option value="nextmonth">{month} (month) </option>
          </select>
        </div>
      </div>

      <div className="row">
        <div className="col-25">
          <label htmlFor="Note">Note</label>
        </div>
        <div className="col-75">
          <textarea
            className="note"
            name="subject"
            placeholder="Write something.."
            style={{ height: "200px" }}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <input
          type="button"
          value="Save"
          className="btn1"
          onClick={() => {
            navigate("/");
            values();
          }}
        />
        <input
          type="button"
          value="Cancel"
          className="btn2"
          onClick={() => navigate("/")}
        />
      </div>
    </div>
  );
}

export default Add;
