import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Add.css";
function Add(props) {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState(new Date());
  const [note, setNote] = useState("");
  const current = new Date(date);


  if (today) {
    const dateh = `${current.getDate()}-${
        current.getMonth() + 1
      }-${current.getFullYear()}`;
}else if (yesterday) {
    const yesterday = new Date(current);
  yesterday.setDate(yesterday.getDate() - 1);
  const yest = `${yesterday.getDate()}-${
    yesterday.getMonth() + 1
  }-${yesterday.getFullYear()}`;
}else if (tomorrow) {
    const tomorrow = new Date(current);
  tomorrow.setDate(tomorrow.getDate() + 1);
  const tom = `${tomorrow.getDate()}-${
    tomorrow.getMonth() + 1
  }-${tomorrow.getFullYear()}`;
}else if (nextWeek) {
    const nextWeek = new Date(current);
  nextWeek.setDate(nextWeek.getDate() + 7);
  const week = `${nextWeek.getDate()}-${
    nextWeek.getMonth() + 1
  }-${nextWeek.getFullYear()}`;
}else if (nextMonth) {
    const nextMonth = new Date(current);
    nextMonth.setDate(nextMonth.getDate() + 30);
    const month = `${nextMonth.getDate()}-${
      nextMonth.getMonth() + 1
    }-${nextMonth.getFullYear()}`; 
}else{

}
  const navigate = useNavigate();
  const values = () => {
    const data = {
      id: Math.random().toString(),
      title: title,
      date: new Date(date),
      note: note,
    };
    props.setData((prevdata) => [...prevdata, data]);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-25">
          <label htmlFor="Title">Title</label>
        </div>
        <div className="col-75">
          <input
            type="text"
            className="Title"
            name="Title"
            placeholder="Your Title.."
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="date">Date</label>
        </div>
        <div className="col-75">
          <input
            type="date"
            className="date"
            name="date"
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="day">Day</label>
        </div>
        <div className="col-75">
          <select className="date-range form-control" name="date" onChange={(e)=>alert(e.target.value)}>
            <option value="Yesterday">{yest} (Yesterday) </option>
            <option selected="selected" value="today">
              {dateh} (Today)
            </option>
            <option value="tomorrow">{tom} (Tomorrow) </option>
            <option value="week">{week} (week) </option>
            <option value="nextmonth">{month} (month) </option>
          </select>
        </div>
      </div>
      <div className="row">
        <div className="col-25">
          <label htmlFor="Note">Note</label>
        </div>
        <div className="col-75">
          <textarea
            className="note"
            name="subject"
            placeholder="Write something.."
            style={{ height: "200px" }}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>
      </div>
      <div className="row">
        <input
          type="button"
          value="Save"
          className="btn1"
          onClick={() => {
            navigate("/");
            values();
          }}
        />
        <input
          type="button"
          value="Cancel"
          className="btn2"
          onClick={() => navigate("/")}
        />
      </div>
    </div>
  );
}

export default Add;
